angular.module("app").directive('accordion', [function () {
    return {
        scope: {
            header: '@',
            asterisk: '@',
            isopen: '@'
        },
        replace: true,
        restrict: 'E',
        transclude: true,
        templateUrl: "kit/directives/accordion/accordion.html",
        link: function (scope) {
            scope.toggleState = function () {
                scope.isopen = scope.isopen ? false : true;
            };
        }
    };
}]);
