angular.module("app").directive("eds", [ '$timeout', '$perf', '$cookie', '$http', '$eds', '$settings', '$rootScope',
 function ($timeout, $perf, $cookie, $http, $eds, $settings, $rootScope) {

    return {
        scope: {
            namespace: "@"
        },
        restrict: "E",
        templateUrl: "kit/directives/eds/eds-template.html",
        link: function ($scope) {
            $eds.init($scope.namespace, $scope);
            $scope.$settings = $settings;
            $scope.$egovChoiceSignService = $rootScope.$egovChoiceSignService; //получаем доступ к сервису выбора способа подписания для того чтобы скрыть кнопку назад в том компоненте
        }
    };
}]);
