// ”станавливает максимальную высоту контейнера в модальном окне
// ≈сли указать значение "force" то помимо max-height установит высоту. ѕример: egov-window-height="force"
// »спользуетс€ при просмотре PDF на подах (устанавливает высоту iframe с pdf)
angular.module("app").directive('egovWindowHeight', ['$window', function ($window) {
    return {
        restrict: 'A',
        scope: true,
        link: function (scope, element, attrs) {
            var paddingPercent = $window.innerHeight < 1000 ? 30 : 15;
            var maxHeight = $window.innerHeight - (($window.innerHeight / 100) * paddingPercent);

            element.css('max-height', maxHeight);

            if (attrs.egovWindowHeight === 'force')
                element.css('height', maxHeight);
        }
    };
}]);