app
    .directive("information", [ "$information", function ($information) {

        return {
            scope: {
                activate: '&',
                priority: '@'
            },
            restrict: "E",
            controller: function ($scope, $element, $q) {
            },
            link: function (scope, element, attrs) {
                var id = uid();
                element.detach();
                scope.$watch('activate()', function () {
                    $information.activate(id, scope.activate());
                });

                $information.store(id, element);
            }
        }
    }]);