angular.module('app')
  .directive('numberbox', [function() {
    return {
      scope: {
        label: '@',
        value: '=',
        required: '@',
        state: '@',
        disabled: '@',
        hint: '@',
        min: '@',
        max: '@'
      },
      replace: true,
      templateUrl: 'kit/directives/numberbox/template.html',
      restrict: "E",
      link: function(scope, elem, attrs) {
        scope.eid = Math.random();
      }
    };
  }]);
