angular.module('egov.ui.textbox', [])
  .directive('textbox', [function() {
    return {
      scope: {
        label: '@',
        value: '=',
        required: '@',
        state: '@',
        disabled: '@',
        hint: '@'
      },
      replace: true,
      templateUrl: 'kit/directives/textbox/template.html',
      restrict: "E",
      link: function(scope, elem, attrs) {
        scope.eid = Math.random();
      }
    }
  }]);
