angular.module("app").service('$error', [ '$rootScope',  function ( $rootScope ) {

    $rootScope.$error = this;

    var message = null, concatMessage = null;

    this.setError = function(text, concat){
        message = arguments[0];
        concatMessage = arguments[1]
    };
    this.getError = function(){
        return message;
    };
    this.getConcatError = function(){
        return concatMessage;
    };
    this.isError = function(){
        return !!message;
    };
    this.resetError = function(){
        message = null;
        concatMessage = null;
    };

    return this;

}]);
