angular.module("app").service('$keyboard', [ '$http', '$q', '$locales', '$document', '$rootScope', '$cookie', function ($http, $q, $locales, $document, $rootScope, $cookie) {

    $rootScope.$keyboard = this;
    var enabled = false;

    enabled = $cookie.get('egov-client-type') == 'POD';

    this.isEnabled = function () {
        return enabled;
    };

    this.activatedFor = function (id) {
        var element = document.getElementById(id);
        if(!!element) return false;
        return element.find('.__kb_focus').length > 0;
    };

    return this;
}]);



