angular.module("app").service('$personalCabinet', ['$rootScope', '$http', '$settings', function ($rootScope, $http, $settings) {

    $rootScope.$personalCabinet = this;

    var personalCabinetUrl = "";

	// Отключил получение ссылки на личный кабинет для ПОД
	if ($settings.isPep()) {
		$http.get('rest/user-panel/base-url').then(function (rs) {
			personalCabinetUrl = rs.data;
		}, function(){
			console.error("SSO URL ERROR");
		});
	}

    this.getPersonalCabinetUrl = function(){
        return personalCabinetUrl;
    };

    return this;
}]);
